public class work1TestTypeDefaultValue {

    public static void main(String[] args) {

        // Auto-generated method stub
        work1TypeDefaultValue type = new work1TypeDefaultValue();
        System.out.println(type.getInt);// 默认值为0
        System.out.println(type.getFloat);// 默认值为0.0
        System.out.println(type.getDouble);// 默认值0.0
        System.out.println(type.getLong);// 默认值为0
        System.out.println(type.getShort);// 默认值为0
        System.out.println(type.getBoolean);// 默认值为 false
        System.out.println(type.getChar);// 默认值为" " 空格
        System.out.println(type.getByte);// 默认值为0
        System.out.println(type.ary1);// 默认值为null
        System.out.println(type.ary2);// 默认值为null

    }
}
